package IfElse;

import java.util.Scanner;

public class Find_char_based_on_Ascii_value {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int n=sc.nextInt();
		sc.close();
		System.out.println((char)n);
	}
}
