package Patterns_from_Basics;

public class Basic_Prgrm_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
			for(int j=1;j<=5;j++) {
				System.out.print(i);
			}
		System.out.println("-");
		}
	}

}
